import './App.css';

// Gets the EXP required to go from stlvl to endlvl with the given "type" of character
async function getExp(stlvl,endlvl,type){

  // Alert if invalid start or end level is given
  if(stlvl === ''){
    alert("Please enter a valid start level (1 - 300)")
  }else if(endlvl === ''){
    alert("Please enter a valid end level (1 - 300)")
  }else{
    await fetch('/exp/'+stlvl+'/'+endlvl+'/'+type)
    .then(response => response.json())
    .then(data => console.log(data.exp));
  }
}

function App() {

  return (
    <div className="container w-1/2 mx-auto flex px-20 py-24 md:flex-row flex-col">


        <input placeholder = "Start Level" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-1/2 p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"type="number" id="stlvl">

        </input>
        <br className="hidden lg:inline-block" />   
        <input placeholder = "End Level" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-1/2 p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"type="number" id="endlvl"/>


      <select id = "type">
        <option value="NONE">NONE</option>
        <option value="MEGA">MEGA</option>
        <option value="TERA">TERA</option>
        <option value="HYPER">HYPER</option>
      </select>
      <button className="bg-indigo-300 hover:bg-orange-400 text-gray-800 font-bold py-2 px-4 rounded inline-flex items-center" onClick={() => getExp(document.getElementById("stlvl").value,document.getElementById("endlvl").value,document.getElementById("type").value)}> 
        <img src="https://www.clipartmax.com/png/middle/270-2702090_maplestory-mushroom-png.png" alt="buttonpng" border="0" />
        <span>Calculate</span>
      </button>



    </div>
  );
}

export default App;
